mapper.update:
update member
set
	me_cookie = #{user.me_cookie},
	me_limit = #{user.me_limit}
where
	me_id = #{user.me_id}

mapper.select:
select question.*, count(qa_num) as qu_answerCount from question 
left join question_answer on qa_qu_num = qu_num 
group by qu_num;

select question.*, count(qa_num) as qu_answerCount from question 
left join question_answer on qa_qu_num = qu_num

	where qu_type = #{qu_type} 

group by qu_num;
